$(document).ready(function(){$.ajax({url:"js/data.json",type:"GET",dataType:"JSON",success:function(o){$.each(o.rows,function(o,n){$("#accordion").append('<div class="accordion-row"><h3>'+n.title+'<img src="/img/drop-down-arrow.png"/></h3><div class="accordion-body" style="display:none"><div>'+n.content+"</div></div></div>")})},error:function(o){console.log(o)}}),$("#accordion").stop().on("click","h3",function(){$("#accordion").find(".accordion-body").slideUp(),$("#accordion").find("h3").removeClass("open"),$(this).parent().find(".accordion-body").slideToggle(),$(this).addClass("open")})});